import Playlist from '../Playlist/Playlist.js';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      searchResults: [
        {
        name: 'Black',
        artist: 'AC/DC',
        album: 'best of',
        id: 1
      },
      {
      name: 'Black',
      artist: 'AC/DC',
      album: 'best of',
      id: 2
    },
    {
    name: 'Black',
    artist: 'AC/DC',
