import React, { Component } from 'react';
import SearchBar from '../SearchBar/SearchBar.js';
import SearchResults from '../SearchResults/SearchResults.js';
import Playlist from '../Playlist/Playlist.js';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      searchResults: [
        {
        name: 'Black1',
        artist: 'AC/DC',
        album: 'best of',
        id: 1
      },
      {
      name: 'Black2',
      artist: 'AC/DC',
      album: 'best of',
      id: 2
    },
    {
    name: 'Black3',
    artist: 'AC/DC',
    album: 'best of',
    id: 3
  }

    ],
    playlistName: 'polarns lista',
    playlistTracks: [
      {
      name: 'Black6',
      artist: 'AC/DC',
      album: 'best of',
      id: 6
    }
    ]
    }
    this.addTrack = this.addTrack.bind(this);
    this.removeTrack = this.removeTrack.bind(this);
    this.updatePlaylistName = this.updatePlaylistName.bind(this);
}

addTrack(track) {
  if (this.state.playlistTracks.find(savedTrack => savedTrack.id === track.id)) {
    return;
  }
  const array = this.state.playlistTracks
  array.push(track);
  this.setState({playlistTracks: array});
}

removeTrack(track) {
  const array = this.state.playlistTracks;
  const index = array.findIndex(x => x.id === track.id)
  array.splice(index,1);
  this.setState({playlistTracks: array});
}

updatePlaylistName(name) {
  this.setState({playlistName: name})
}


  render() {
    return (
      <div>
        <h1>Ja<span className="highlight">mmm</span>ing</h1>
        <div className="App">
          {/*//<!-- Add a SearchBar component -->*/}
          <div className="App-playlist">
            <SearchResults searchResults={this.state.searchResults} onAdd={this.addTrack} isRemoval={false} />
            <Playlist playlistName={this.state.playlistName} playlistTracks={this.state.playlistTracks} onRemove={this.removeTrack} isRemoval={true} onNameChange={this.updatePlaylistName} />

          </div>
        </div>
      </div>
    );
  }
}

export default App;
